import React from "react";
import * as sessionActions from "../../store/session";
import { useDispatch, useSelector } from "react-redux";
import { Redirect } from "react-router-dom";
import { useState } from "react";
import googleLogo from "../../assets/GlooGle2.svg";
import { Link } from "react-router-dom";

const SignUpForm = () => {
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(
      sessionActions.signup({ username, email, password, firstName, lastName })
    );
  };
  return (
    <div className="page-container">
      <div className="signin-container">
        <figure className="logo-container">
          <img className="gloogle-logo" src={googleLogo} />
        </figure>
        <h2 className="signin-header">Sign in</h2>
        <p className="signin-subheader"> to continue to GluetTube</p>
        <form className="signin-form" onSubmit={handleSubmit}>
          <ul>
            {errors.map((error, idx) => (
              <li className="err-message" key={idx}>
                {error}
              </li>
            ))}
          </ul>
          {!showPassword ? (
            <>
              <input
                className="signin-input email-input"
                type="text"
                value={credential}
                onChange={(e) => setCredential(e.target.value)}
                placeholder="Username or Email"
                required
              />

              <button
                className="next-button"
                type="submit"
                onClick={() => setShowPassword(true)}
              >
                Next
              </button>
              <div className="signup-container">
                <Link to="/signup" className="signup-link">
                  Create account
                </Link>
              </div>
            </>
          ) : (
            <>
              <button
                className="back-button"
                type="button"
                onClick={() => setShowPassword(false)}
              >
                Back
              </button>
              <input
                className="signin-input password-input"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                required
              />
              <button className="signin-button" type="submit">
                Sign In
              </button>
            </>
          )}
        </form>
      </div>
    </div>
  );
};

export default SignUpForm;
